<?phpnamespace app\cner\validate;use think\Validate;use app\cner\Admin as Model;class Admin extends Validate{		protected $rule=[		//管理员登录		['adminEmail','require','邮箱不能为空'],		['adminPwd','require','密码不能为空'],		['vertify','require|captcha','验证码不能为空|验证码错误'],		//添加管理员		['admin_name','require|max:15','管理员名不能为空|名字过长'],		['admin_pwd','require|max:50','密码不能为空|密码过长'],		['admin_pwd_confirm','require|confirm','验证密码不能为空|两次密码不相同'],		['admin_email','require|email|unique:fdc_admin','邮箱不能为空|邮箱格式不正确|邮箱已经使用'],		//编辑管理员		['pre_admin_pwd','require','原始密码不能为空'],		['new_admin_pwd','require|max:15','新密码不能为空|密码长度过长'],		['new_admin_pwd_confirm','require|confirm','验证密码不能为空|两次密码不一致'],		//添加文章		['article_title','require','题目不能为空'],		['article_content','require','内容不能为空'],		['article_des','require','简介不能为空'],				//地铁添加		['subway_num','require|number|unique:fdc_subway','线路不可为空|必须为数字|线路已经存在'],		['subway_name','require','显示内容不可为空'],		//面积添加		['area_num','require|number|unique:fdc_area','面积不可为空|必须为数字|面积已经存在'],		['area_name','require','显示内容不可为空'],		//租金添加		['renprice_num','require|number|unique:fdc_renprice','租金不可为空|必须为数字|租金已经存在'],		['renprice_name','require','显示内容不可为空'],		//售价添加		['saprice_num','require|number|unique:fdc_saprice','售价不可为空|必须为数字|售价已经存在'],		['saprice_name','require','显示内容不可为空'],];						protected $scene=[		'adminLogin'=>['adminEmail','adminPwd','vertify'],		'adminAdd'=>['admin_name','admin_pwd','admin_pwd_confirm','admin_email'],		'adminEdit'=>['pre_admin_pwd','new_admin_pwd','new_admin_pwd_confirm'],		'articleAdd'=>['article_title','article_content','article_des'],		'subwayAdd'=>['subway_num','subway_name'],		'areaAdd'=>['area_num','area_name'],		'renpriceAdd'=>['renprice_num','renprice_name'],		'sapriceAdd'=>['saprice_num','saprice_name'],					];}