-- ----------------------------
-- Table structure for `fdc_employ`
-- ----------------------------
DROP TABLE IF EXISTS `fdc_employ`;
CREATE TABLE `fdc_employ` (
  `employ_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `employ_title` varchar(255) NOT NULL,
  `employ_address` varchar(255) NOT NULL,
  `employ_num` int(11) NOT NULL,
  `employ_store` varchar(255) NOT NULL,
  `employ_creatime` int(11) NOT NULL,
  `employ_des` longtext NOT NULL,
  `identifier` smallint(6) NOT NULL DEFAULT '0' COMMENT '编号',
  `is_left` tinyint(3) DEFAULT '0' COMMENT '0：不在左边;1：在左边',
  PRIMARY KEY (`employ_id`)
) ENGINE=MyISAM AUTO_INCREMENT=48 DEFAULT CHARSET=utf8;

