-- ----------------------------
-- Table structure for `fdc_entrust`
-- ----------------------------
DROP TABLE IF EXISTS `fdc_entrust`;
CREATE TABLE `fdc_entrust` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL DEFAULT '',
  `phone` varchar(100) NOT NULL DEFAULT '',
  `address` varchar(255) DEFAULT NULL,
  `create_time` int(11) NOT NULL DEFAULT '0',
  `des` text,
  `img` varchar(255) DEFAULT NULL,
  `type` tinyint(3) NOT NULL DEFAULT '0' COMMENT '0:出租；1:出售；2:求租；3:求售；4:报事；5:报修；6:建议；7:投诉；8:租赁；买卖',
  `home_name` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
