-- ----------------------------
-- Records of fdc_planning
-- ----------------------------
INSERT INTO `fdc_planning` VALUES ('1', '<p><strong>公司使命：</strong>聚焦为客户提供优质的产品和服务并丰富生活，聚焦为奋斗者提供自我实现的平台并创造价值，在此过程中，成为最受喜爱的企业并创造机会。</p><p><img alt=\"1516764485136247.jpg\" src=\"/ueditor/php/upload/image/20180124/1516764485136247.jpg\"/></p><p><strong>核心价值观：</strong>英地的气质：“<strong>志存高远，求知谦虚</strong>”。这种气质由以下五项核心价值观支撑：<strong>精英、厚爱、进化、互信、志存高远</strong></p><p><strong><br/></strong></p>', '组织文化');

